"use client";
// import { Metadata } from "next";
import Link from "next/link";
import "./style.css";
import { usePathname } from "next/navigation";

// export const metadata: Metadata = {
//   title: {
//     absolute: "",
//     default: "next app",
//     template: "%s | Next App",
//   },
//   description: "Generated by Next App",
// }
const navLink = [
  {name: "Blog", href:"/blog"},
  {name: "Dashboard", href:"/dashboard"},
  {name: "About", href:"/about"},
  {name: "Profile", href:"/profile"},
  {name: "Login", href: "/login"},
];

export default function RootLayout({
  children,
}: {
  children: React.ReactNode
}) {
  const pathname = usePathname();
  return (
    <html lang="en">
      <body className={"bg-slate-900 bg-blend-darken text-white relative"}>
        <header className={"mx-20 px-10 py-4"}>
          <Link href="/" className={"font-bold text-2xl"}>Navbar</Link>
         
            {navLink.map((link) => {const isActive = pathname.startsWith(link.href);
              return (
                <Link 
                href={link.href} 
                key={link.name} 
                className={isActive ? "font-bold m-4" : "text-slate-200 m-4"}>
                  {link.name} 
                </Link>
              );
            })}
        </header>
        <div className={"container p-5 ml-5 mr-5"}>
        {children}
        </div>
        <footer className={"fixed bottom-0 p-5 mx-10 mt-5"}>
          <div className={"text-center"}>
            <h1>copyright</h1>
          </div>
        </footer>
      </body>
    </html>
  )
}
